pragma solidity ^0.8.0;

import {IPool, IERC20} from "../interfaces/IPool.sol";
import {IConfiguration} from "../interfaces/IConfiguration.sol";

interface IBridge {
    enum ErrorCode {
        RESOLVED,
        FAILED
    }

    struct TokenAmount {
        IERC20 token;
        uint256 amount;
    }

    struct FailedMessageContent {
        address receiver;
        TokenAmount tokenAmount;
    }

    struct FailedMessage {
        bytes32 messageId;
        ErrorCode errorCode;
    }

    event CrossChainMessageSent(
        bytes32 indexed messageId,
        address indexed sender,
        address indexed receiver
    );
    event TokensTransferred(
        IERC20 indexed tokenSource,
        IPool indexed tokenSourcePool,
        uint64 indexed destinationChainSelector,
        IERC20 tokenDestination,
        uint256 amount,
        uint256 fees
    );

    event CrossChainMessageReceived(
        bytes32 indexed messageId,
        address indexed sender,
        address indexed receiver
    );

    event TokensReceived(
        IERC20 indexed tokenDestination,
        IPool indexed tokenDestinationPool,
        uint64 indexed sourceChainSelector,
        uint256 amount
    );
    event MessageFailed(
        bytes32 indexed messageId,
        address indexed receiver,
        TokenAmount tokenAmount,
        bytes reason
    );
    event MessageRecovered(
        bytes32 indexed messageId,
        address indexed recoverer,
        address indexed receiver,
        TokenAmount tokenAmount
    );
    event ConfigurationSet(
        IConfiguration indexed oldConfiguration,
        IConfiguration indexed newConfiguration,
        address indexed owner
    );
    event RouterSet(
        address indexed oldRouter,
        address indexed newRouter,
        address indexed owner
    );

    error InvalidRouter(address router);
    error DestinationChainSelectorIsZero();
    error OnlySelf(address caller);
    error TokenNotSupported(IERC20 token, uint64 destinationChainSelector);
    error TokenPoolNotSet(IERC20 token);
    error NoBridgeOnDestination(uint64 destinationChainSelector);
    error InvalidSenderBridge(
        uint64 sourceChainSelector,
        address configuredSenderBridge,
        address senderBridge
    );
    error ReceiverAddressIsZero();
    error CannotSetFeeTokenIfNativeGasNotZero(
        uint256 nativeGas,
        address feeToken
    );

    error ConfigurationNotSet();
    error ConfigurationIsZero();
    error TokenAddressIsZero();
    error FeeTokenAddressIsZero();
    error NoExtraArgs(uint64 destinationChainSelector);
    error InsufficientFees(uint256 nativeGas, uint256 fees);
    error InsufficientFeeAllowance(
        IERC20 feeToken,
        uint256 fees,
        uint256 allowance
    );
    error FailedToSendChange(address receiver, uint256 changeAmount);
    error ReceivedAmountIsZero();
    error MessageNotFailed(bytes32 messageId);
    error ConfigurationAlreadySet(address configuration);

    function setConfiguration(IConfiguration configuration) external;

    function getConfiguration()
        external
        view
        returns (IConfiguration configuration);

    function setRouter(address router) external;

    function getRouter() external view returns (address router);

    function getFee(
        uint64 destinationChainSelector,
        IERC20 token,
        uint256 amount,
        address receiver,
        IERC20 feeToken
    ) external view returns (uint256 fees);

    function transferTokensToDestinationChain(
        uint64 destinationChainSelector,
        IERC20 token,
        uint256 amount,
        address receiver,
        IERC20 feeToken
    ) external payable returns (bytes32 messageId, uint256 fees);

    function retryFailedMessage(
        bytes32 messageId,
        address tokenReceiver
    ) external;

    function getFailedMessages(
        uint256 offset,
        uint256 limit
    ) external view returns (FailedMessage[] memory failedMessages);

    function getFailedMessageContent(
        bytes32 messageId
    ) external view returns (FailedMessageContent memory messageContent);
}
","keccak256":"0xa65a81a28afa17208ebc3004cc19f6a9d2025cb6e994289d5b18305cd6a06a98","license":"MIT"},"contracts/interfaces/IConfiguration.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import {IPool} from "../interfaces/IPool.sol";
import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";

interface IConfiguration {
    struct ConfigOut {
        address receiverBridge;
        IERC20 destinationToken;
        IPool pool;
        bytes extraArgs;
    }

    struct ConfigIn {
        address senderBridge;
        IPool pool;
    }

    event RemoteBridgeSet(
        uint64 indexed chainSelector,
        address indexed remoteBridge,
        address indexed owner
    );

    event RemoteBridgeRemoved(
        uint64 indexed chainSelector,
        address indexed owner
    );

    event ExtraArgsSet(
        uint64 indexed destinationChainSelector,
        bytes extraArgs,
        address indexed owner
    );

    event DestinationTokenSet(
        IERC20 indexed sourceToken,
        uint64 indexed destinationChainSelector,
        IERC20 destinationToken,
        address indexed owner
    );

    event DestinationTokenRemoved(
        IERC20 indexed sourceToken,
        uint64 indexed destinationChainSelector,
        address indexed owner
    );

    event TokenPoolSet(
        IERC20 indexed token,
        IPool indexed pool,
        address indexed owner
    );
    event TokenPoolRemoved(IERC20 indexed token, address indexed owner);

    error PoolAddressIsZero();
    error TokenAddressIsZero();
    error BridgeAddressIsZero();

    function setRemoteBridge(
        uint64 chainSelector,
        address remoteBridge
    ) external;

    function removeRemoteBridge(uint64 chainSelector) external;

    function setExtraArgs(
        uint64 destinationChainSelector,
        bytes memory extraArgs
    ) external;

    function setTokenPool(IPool pool) external;

    function removeTokenPool(IERC20 token) external;

    function setDestinationToken(
        IERC20 sourceToken,
        uint64 destinationChainSelector,
        IERC20 destinationToken
    ) external;

    function removeDestinationToken(
        IERC20 sourceToken,
        uint64 destinationChainSelector
    ) external;

    function isTokenAvailableOnDestination(
        IERC20 token,
        uint64 destinationChainSelector
    ) external view returns (bool available);

    function isBridgeAvailableOnChain(
        uint64 chainSelector
    ) external view returns (bool available);

    function getRemoteBridge(
        uint64 chainSelector
    ) external view returns (address remoteBridge);

    function getExtraArgs(
        uint64 destinationChainSelector
    ) external view returns (bytes memory extraArgs);

    function getTokenPool(IERC20 token) external view returns (IPool pool);

    function getDestinationToken(
        IERC20 sourceToken,
        uint64 destinationChainSelector
    ) external view returns (IERC20 destinationToken);

    function isTokenPoolAvailable(
        IERC20 token
    ) external view returns (bool available);

    function getConfigOut(
        IERC20 token,
        uint64 destinationChainSelector
    ) external view returns (ConfigOut memory config);

    function getConfigIn(
        IERC20 token,
        uint64 sourceChainSelector
    ) external view returns (ConfigIn memory config);
}
","keccak256":"0x51b114cfb6cafccd12884fa30ba77a99dd8c89175ee4cb6247a89274efcc9cd4","license":"MIT"},"contracts/interfaces/IPool.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";

interface IPool {
    event BridgeSet(
        address indexed oldBridge,
        address indexed newBridge,
        address indexed owner
    );
    event Locked(address indexed sender, uint256 amount);
    event Burned(address indexed sender, uint256 amount);
    event Released(
        address indexed sender,
        address indexed receiver,
        uint256 amount
    );
    event Minted(
        address indexed sender,
        address indexed receiver,
        uint256 amount
    );

    error NoMoreLiquidity(uint256 amountoRelease, uint256 liquidity);

    error IsNotBridge(address caller, address bridge);

    error BridgeAddressZero();

    enum TokenPoolType {
        LockRelease,
        BurnMint
    }

    /**
     * @notice Locks or burns the amount of tokens.
     * @notice The caller must have the role of locker for LockRelease token pool or burner for BurnMint token pool.
     * @param amount The amount of tokens to lock or burn
     */
    function lockOrBurn(uint256 amount) external;

    /**
     * @notice Releases or mints the amount of tokens.
     * @notice The caller must have the role of unlocker for LockRelease token pool or minter for BurnMint token pool.
     * @dev For LockRelease token pool, if the pool is out of liquidity, it reverts.
     * @param amount The amount of tokens to release or mint
     * @param receiver The address to receive the released or minted tokens
     */
    function releaseOrMint(uint256 amount, address receiver) external;

    /**
     * @notice Returns the token of the pool.
     * @return token The token of the pool
     */
    function getToken() external view returns (IERC20 token);

    /**
     * @notice Returns the type of the token pool.
     * @return tokenPoolType The type of the token pool
     */
    function getTokenPoolType()
        external
        view
        returns (TokenPoolType tokenPoolType);

    /**
     * @notice Sets `account` as the bridge. Only callable by the owner.
     * @dev Emits the `BridgeSet` event upon success.
     * @param account The account to set as the bridge.
     */
    function setBridge(address account) external;

    /**
     * @notice Removes the bridge. Only callable by the owner.
     * @dev Emits the `BridgeSet` event with bridge as 0 address upon success.
     */
    function removeBridge() external;

    /**
     * @notice Returns the bridge address.
     * @return bridge The bridge address
     */
    function getBridge() external view returns (address bridge);
}
